INSERT INTO "grb_playstatus" (
    "group", "item_id", "playCount", "bookMarkPos"
) SELECT
    'default', "id", 1, "bookmark_pos"
FROM "mt_cds_object" where "bookmark_pos" > 0 OR ("flags" & 0x00000200) != 0;

